        journal: "Chimia",
        year: 2016,
        keywords: "Cosmetics, Humans, Phenols, Radiation-Protective Agents, Skin Aging, Triazines, Ultraviolet Rays",
        url: "https://pubmed.ncbi.nlm.nih.gov/27561611/",
